INSERT INTO "ricette" VALUES (8256,'Decotto Di Rosa Canina','Bevande','Rosa Canina',1,' -','30 G Frutti Freschi Di Rosa Canina A Pezzetti (o Frutti Secchi Di Rosa Canina A Pezzetti),
100 Cl Acqua','Mettere a macerare i frutti nell''acqua a freddo per 60 minuti. Far bollire poi per 10 minuti e riposare per 15 minuti. Utile contro l''angina: 100 cl al giorno.',16);
INSERT INTO "ricette" VALUES (8257,'Decotto Di Rovo','Bevande','Rovo',4,' -','100 G Foglie Di Rovo Fresche,
100 Cl Acqua','bollire 100 g di foglie di rovo fresche in 100 cl d''acqua per 20 minuti. Utile per l''afta.',16);
INSERT INTO "ricette" VALUES (8258,'Decotto Di Salsapariglia','Bevande','Salsapariglia',1,' -','40 G Radici Essiccate Di Salsapariglia,
