INSERT INTO "ricette" VALUES (8259,'Decotto Di Sambuco','Bevande','Sambuco',1,' -','50 G Fiori E Frutti Di Sambuco,
100 Cl Acqua','Bollire fiori, frutti e acqua fino a ridurli alla metà. Utile contro l''arteriosclerosi: bere 3 volte al giorno 30 cl di decotto.',11);
